import React from 'react';
import { Link } from 'react-router-dom';

function HeroSection() {
  return (
    <section className="bg-blue-600 text-white py-20">
      <div className="container mx-auto px-4 text-center">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">Create Professional Directories with Ease</h1>
        <p className="text-xl mb-8">Get instant access to our powerful Directory Maker boilerplate</p>
        <Link to="/pricing" className="bg-white text-blue-600 py-3 px-8 rounded-full font-bold hover:bg-blue-100 transition duration-300">Buy Now - $79</Link>
      </div>
    </section>
  );
}

export default HeroSection;
