import React from 'react';
import { Link } from 'react-router-dom';

function HeroSection() {
  return (
    <section className="hero bg-blue-700 text-white py-20">
      <div className="container mx-auto text-center px-4">
        <h1 className="text-5xl font-bold mb-4">Create Professional Directories with Ease</h1>
        <p className="text-xl mb-8">Get instant access to our powerful Directory Maker boilerplate</p>
        <Link to="/pricing" className="cta-button py-3 px-8 rounded-full font-bold transition duration-300">
          Buy Now - $79
        </Link>
      </div>
    </section>
  );
}

export default HeroSection;
