import React from 'react';

function FeaturesSection() {
  const features = [
    { title: 'Ready-to-use directory code', description: 'Jump-start your project with our professionally crafted boilerplate.' },
    { title: 'Secure access through GitHub', description: 'Access your code securely through a private GitHub repository.' },
    { title: 'One-time payment', description: 'No subscriptions, just a single payment for lifetime access.' },
    { title: 'Customizable', description: 'Easily modify and extend the code to fit your specific needs.' }
  ];

  return (
    <section className="features">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">Features</h2>
        <div className="feature-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-item">
              <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
              <p className="text-gray-700">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default FeaturesSection;
