import React from 'react';

function FAQSection() {
  const faqs = [
    {
      question: 'What is included in the purchase?',
      answer: 'You get full access to the Directory Maker boilerplate code via a private GitHub repository.'
    },
    {
      question: 'How do I access the GitHub repository?',
      answer: 'After your purchase, you\'ll be prompted to enter your GitHub username. We\'ll then send an invitation to that account.'
    },
    {
      question: 'Can I modify the code?',
      answer: 'Yes, you have full rights to modify and use the code for your projects.'
    },
    {
      question: 'What kind of support is available?',
      answer: 'We offer basic support through GitHub issues. For more extensive support, please contact us directly.'
    }
  ];

  return (
    <section className="py-20 bg-gray-100">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-12">Frequently Asked Questions</h2>
        <div className="max-w-3xl mx-auto">
          {faqs.map((faq, index) => (
            <div key={index} className="mb-8">
              <h3 className="text-xl font-semibold mb-2">{faq.question}</h3>
              <p className="text-gray-600">{faq.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default FAQSection;
