import React from 'react';

function HeroSection() {
  return (
    <section className="bg-gray-100 py-16">
      <div className="container mx-auto px-4">  {/* Added container and px-4 for padding */}
        <div className="text-center">
          <h1 className="text-5xl font-bold mb-4 text-gray-900">Create Professional Directories with Ease</h1>
          <p className="text-xl text-gray-700 mb-8">Get instant access to our powerful Directory Maker boilerplate</p>
          <a href="#buy" className="bg-blue-500 text-white py-3 px-8 rounded-full hover:bg-blue-600">
            Buy Now - $79
          </a>
        </div>
      </div>
    </section>
  );
}

export default HeroSection;
